          />
        </div>

        <div className="charts-section">
          <Chart 
            data={history} 
            title="Hist√≥rico do Potenci√¥metro"
            height={300}
          />
        </div>

        <div className="history-section">
          <HistoryList 
            history={history} 
            onClear={clearHistory}
          />
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
